import * as SQLite from "expo-sqlite";

export const openDatabase = () => {
  return SQLite.openDatabase('dictionary.db');
};

export const createTable = async (db: any) => {
  // create table if not exists
  const history = `CREATE TABLE IF NOT EXISTS history(
        word TEXT NOT NULL
        order INT
    );`;
    const insert = `INSERT INTO history (word,order) VALUES('alien',1)`;
  await db.executeSql(history);
  await db.executeSql(insert);
  const bookmark = `CREATE TABLE IF NOT EXISTS bookmark(
    word TEXT NOT NULL
    order INT
);`; 
await db.executeSql(bookmark);
};

export const getWordHistory = async (db: SQLiteDatabase): Promise<[]> => {
    try {
      let wordHistory = [] as any;
      const results = await db.executeSql(`SELECT rowid as id,word,order FROM history`);
      results.forEach(result => {
        for (let index = 0; index < result.rows.length; index++) {
            wordHistory.push(result.rows.item(index))
        }
      });
      console.log('word',wordHistory);
      return wordHistory;
    } catch (error) {
      console.error(error);
      throw Error('Failed to get WordHistory !!!');
    }
};
