import * as WebBrowser from 'expo-web-browser';
import { StyleSheet, TouchableOpacity, ColorSchemeName, Pressable } from 'react-native';

import Colors from '../constants/Colors';
import { MonoText } from '../components/StyledText';
import { Text, View } from '../components/Themed';
import { FontAwesome } from '@expo/vector-icons';
import useColorScheme from '../hooks/useColorScheme';
import { useEffect, useState } from 'react';
import { getWordHistory, getWordHistoryByLocation, openDatabase } from '../db';
export default function HistoryInfo({ navigation, path }: { navigation: any, path: string }) {
  const [data,setData] = useState([]);
  useEffect(() => {
    (async () =>{
      console.log('history');
      const db = await openDatabase();
      let wordHistory = await getWordHistoryByLocation(db);
      setData(wordHistory);
    })();
  },[])
  console.log('wordHistory',data);
  return (
    <View>
      {!!data && data.length!=0 && data.map((value: any, key: any)=>{
        return (
        <Text key={key} style={styles.getStartedText}>
          {value.word}
        </Text>)
      })}
    </View>
  );
}


const styles = StyleSheet.create({
  getStartedContainer: {
    alignItems: 'center',
    marginHorizontal: 50,
  },
  homeScreenFilename: {
    marginVertical: 7,
  },
  codeHighlightContainer: {
    borderRadius: 3,
    paddingHorizontal: 4,
  },
  getStartedText: {
    fontSize: 20,
    lineHeight: 30,
    textAlign: 'center',
    marginBottom: 20

  },
  helpContainer: {
    marginTop: 15,
    marginHorizontal: 20,
    alignItems: 'center',
  },
  helpLink: {
    paddingVertical: 15,
  },
  helpLinkText: {
    textAlign: 'center',
  },
});
