    //           const db = await openDatabase();


    //           const word = await getWordBookmarkByWord(db, item);
    //           console.log('tim dc', item,word)
    //           if (word) {
    //             setFavorite(true);
    //           }
    //         }
    //       }
    //     }).catch(err => {
    //       setMounted(false)
    //       setErr(err.response.data.message);
    //     })
    //   })();
    // }

      setTitle(value);
      (async () => {
        await axios.get(api).then(async res => {
          if (res.data) {
            if (isMounted) {
              setData(res.data); setMounted(false);
            };
          }
        }).catch(err => {
          setMounted(false)
          if (err.response)
          setErr(err.response.data.message);
        })
      })();

    return () => {
      setMounted(false);
    };
  }, [])
  return (

    <ScrollView style={styles.container}>
      {!!isMounted && <ActivityIndicator color={"#fff"} />}
      <Text style={styles.title}>{title}
        {!!data && <Pressable
          onPress={async () => {
            setFavorite(!favorite);
            if (favorite) {
              const db = await openDatabase();
              await deleteBookmark(db,title);

            }
            else {
              const db = await openDatabase();
              await insertBookmark(db,title);

            }
          }}
          style={({ pressed }) => ({
            opacity: pressed ? 0.5 : 1,
          })}>
          {!favorite ?
            <FontAwesome
              name="star-o"
              size={25}
              color='#FFFFFF'
              style={{ marginRight: 15, marginLeft: 10 }}
            /> : <FontAwesome
              name="star"
              size={25}
              color='#FFFFFF'
              style={{ marginRight: 15, marginLeft: 10 }}
            />
          }
        </Pressable>
        }
      </Text>
      <View style={styles.separator} lightColor="#eee" darkColor="rgba(255,255,255,0.1)" />
      {!!data && <SearchTextInfo data={data} path="/screens/ModalScreen.tsx" />}
      {data && <Text style={styles.title}>{err}</Text>}
      {/* Use a light status bar on iOS to account for the black space above the modal */}
      <StatusBar style={Platform.OS === 'ios' ? 'light' : 'auto'} />
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: 10
  },
  title: {
    fontSize: 20,
    textAlign: 'center',
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 10,
    height: 1,
    width: '80%',
  },
});
