import * as WebBrowser from 'expo-web-browser';
import { StyleSheet, TouchableOpacity,Pressable } from 'react-native';

import Colors from '../constants/Colors';
import { MonoText } from './StyledText';
import { Text, View } from './Themed';
import { FontAwesome } from '@expo/vector-icons';
export default function SearchTextInfo({ data, path }: { data: any,path: string }) {


  const wordInfo = {
    word: "tin",
    phonetic: "/tɪn/",
    phonetics: [
      {
        text: "/tɪn/",
        audio: "https://api.dictionaryapi.dev/media/pronunciations/en/tin-uk.mp3",
        sourceUrl: "https://commons.wikimedia.org/w/index.php?curid=11037"
      },
      {
        text: "/tɪn/",
        audio: "https://api.dictionaryapi.dev/media/pronunciations/en/tin-us.mp3",
        sourceUrl: "https://commons.wikimedia.org/w/index.php?curid=1197440",
        license: {
          name: "BY-SA 3.0",
          url: "https://creativecommons.org/licenses/by-sa/3.0"
        }
      }
    ],
    meanings: [
      {
        partOfSpeech: "noun",
        definitions: [
          {
            definition: "A malleable, ductile, metallic element, resistant to corrosion, with atomic number 50 and symbol Sn.",
            synonyms: [],
            antonyms: []
          },
          {
            definition: "An airtight container, made of tin or another metal, used to preserve food.",
            synonyms: [],
            antonyms: []
          },
          {
            definition: "A metal pan used for baking, roasting, etc.",
            synonyms: [],
            antonyms: [],
            example: "muffin tin"
          },
          {
            definition: "The bottom part of the front wall, which is \"out\" if a player strikes it with the ball.",
            synonyms: [],
            antonyms: []
          },
          {
            definition: "Money",
            synonyms: [],
            antonyms: []
          },
          {
            definition: "Computer hardware.",
            synonyms: [],
            antonyms: []
          }
        ],
        synonyms: [
          "can",
          "tin can"
        ],
        antonyms: []
      },
      {
        partOfSpeech: "verb",
        definitions: [
          {
            definition: "To place into a tin in order to preserve.",
            synonyms: [],
            antonyms: []
          },
          {
            definition: "To cover with tin.",
            synonyms: [],
            antonyms: []
          },
          {
            definition: "To coat with solder in preparation for soldering.",
            synonyms: [],
            antonyms: []
          }
        ],
        synonyms: [],
        antonyms: []
      },
      {
        partOfSpeech: "adjective",
        definitions: [
          {
            definition: "Made of tin.",
            synonyms: [],
            antonyms: []
          },
          {
            definition: "Made of galvanised iron or built of corrugated iron.",
            synonyms: [],
            antonyms: []
          }
        ],
        synonyms: [
          "tinnen"
        ],
        antonyms: []
      }
    ],
    license: {
      name: "CC BY-SA 3.0",
      url: "https://creativecommons.org/licenses/by-sa/3.0"
    },
    sourceUrls: [
      "https://en.wiktionary.org/wiki/tin"
    ]
  };
  console.log('word info',wordInfo);
  return (
    <View>
      <View style={styles.getStartedContainer}>
        <Text
          style={styles.getStartedText}
          lightColor="rgba(0,0,0,0.8)"
          darkColor="rgba(255,255,255,0.8)">
          <b>Pronunciation</b> {wordInfo.phonetic}
        </Text>
        {wordInfo.phonetics && wordInfo.phonetics.map((value : any, key: any)=>{
            console.log(value,key);
            return (
              <div style={{display: "flex"}}>
              <Text
              style={styles.getStartedText}
              lightColor="rgba(0,0,0,0.8)"
              darkColor="rgba(255,255,255,0.8)">
              <i>{key==0?"(enUK)":"(enUS)"}</i> : {value.text}
            </Text>
            <Pressable
              // onPress={() => navigation.navigate('Modal')}
            >

              <FontAwesome
                name="file-audio-o"
                size={18}
                style={{ marginLeft: 15 }}
                color="#FFFFFF"
              />
            </Pressable>
            </div>
            )
        })}
        <View
          style={[styles.codeHighlightContainer, styles.homeScreenFilename]}
          darkColor="rgba(255,255,255,0.05)"
          lightColor="rgba(0,0,0,0.05)">
          <MonoText>{path}</MonoText>
        </View>

        <Text
          style={styles.getStartedText}
          lightColor="rgba(0,0,0,0.8)"
          darkColor="rgba(255,255,255,0.8)">
          Change any of the text, save the file, and your app will automatically update.
        </Text>
      </View>
    </View>
  );
}

function handleHelpPress() {
  WebBrowser.openBrowserAsync(
    'https://docs.expo.io/get-started/create-a-new-app/#opening-the-app-on-your-phonetablet'
  );
}

const styles = StyleSheet.create({
  getStartedContainer: {
    marginHorizontal: 50,
  },
  homeScreenFilename: {
    marginVertical: 7,
  },
  codeHighlightContainer: {
    borderRadius: 3,
    paddingHorizontal: 4,
  },
  getStartedText: {
    fontSize: 13,
    lineHeight: 15,
  },
  helpContainer: {
    marginTop: 15,
    marginHorizontal: 20,
  },
  helpLink: {
    paddingVertical: 15,
  },
  helpLinkText: {
  },
});
